 investmentViewModel = investmentViewModel,
 range = range,
 hideMarkerTrigger = hideMarkerTrigger,
 onHideMarkers = { hideMarkerTrigger++ }
 )
 }

 BalancePieChart(
 viewModel = databaseViewModel,
 startDate = range.start.toDouble(),
 endDate = range.endInclusive.toDouble()
 )
 }
 }
 }
